
CREATE TABLE `seller_settlement_rules` (

	`rule_id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
	`order_status` TINYINT(3) UNSIGNED DEFAULT NULL,
	`shipment_status` TINYINT(3) UNSIGNED DEFAULT NULL,
	`return_status` TINYINT(3) UNSIGNED DEFAULT NULL,
	`detail_id` TINYINT(3) UNSIGNED NOT NULL,
	`detail_type` TINYINT(3) UNSIGNED NOT NULL DEFAULT 0,
	`detail_calculation` VARCHAR(64) DEFAULT NULL,
	`tax_applicable` TINYINT(3) UNSIGNED NOT NULL DEFAULT 1,
	`applicable_on` VARCHAR(16) NOT NULL,
	`applicable_when` VARCHAR(64) DEFAULT NULL,
	`applicable_from` DATE NOT NULL DEFAULT "2016-01-01",
	`applicable_to` DATE NOT NULL DEFAULT "2099-12-31",
	`settlement_item_status` TINYINT(3) UNSIGNED NOT NULL,
	`prepaid_settle_date` TINYINT(3) DEFAULT NULL,
	`cod_settle_date` TINYINT DEFAULT NULL,
	`rule_status` TINYINT(3) UNSIGNED NOT NULL DEFAULT 0,
	`date_added` DATETIME NOT NULL,
	`date_modified` DATETIME NOT NULL,
	PRIMARY KEY (`rule_id`),
	INDEX (`applicable_from`, `applicable_to`),
	INDEX (`order_status`, `shipment_status`, `return_status`)

) ENGINE = INNODB;

;